			private void _VCN_OnAfterValueChanged(object sender, ValueChangedEventArgs e)
			{
				if (null == OnValueChangedCallBack) return;
				control?.e_RunInUIThread(() => OnValueChangedCallBack?.Invoke(e.NewValue!));
