			{
				_ = a ?? throw new ArgumentNullException(nameof(a));

				Action a2 = delegate
				{
					lvw?.BeginUpdate();
					try { a!.Invoke(); }
					finally
					{
						if (autoSizeColumns) lvw?.e_AutoSizeColumns();
						if (fastUpdateGroupHeaders) lvw?.e_SetGroupsTitlesFast();
