			{
				ListView? lvw = li.ListView as ListView;

				lvw?.e_runOnLockedUpdate(delegate
				{
					if (deactivateOther)
						lvw?.e_ItemsAsIEnumerable().ToList().ForEach(li => li.Selected = false);

					li.Selected = true;
					li.Focused = true;
					li.EnsureVisible();
				});
			}
		}


