						int iErrorsFound = 0;
						var PrepareAllErrorsInfoCallBack = new Func<string?>(() =>
						 {
							 var aErrorsToShow = Array.Empty<Exception>();
							 lock (_qErrorsToShow) // Занимаем очередь, начинаем ждать освобождения занятой очереди
							 {
