				{
					using (Pen p = new Pen(Color.FromArgb((int)alpha, 0, 0, 0), thickness))
					{
						p.LineJoin = LineJoin.Round;
						g.DrawPath(p, gp);
					}
					alpha += astep;
					astep += astepstep;
				}
			}


