			/// <summary>Центрирует прямоугольник относительно заданной точки</summary>
			[MethodImpl(MethodImplOptions.AggressiveInlining)]
			internal static Rectangle e_AlignTo(this Rectangle src, Rectangle trg, ContentAlignment alignment, Point? offset = null)
			{
				Point ptLocation = src.Location;

				{
					//Calculate Y
					switch (alignment)
					{
						case ContentAlignment.TopLeft:
						case ContentAlignment.TopCenter:
						case ContentAlignment.TopRight:
							{
								ptLocation.Y = trg.Y;
								if (offset.HasValue) ptLocation.Y += offset.Value.Y;
								break;
							}
					}

					switch (alignment)
					{
						case ContentAlignment.MiddleLeft:
						case ContentAlignment.MiddleCenter:
						case ContentAlignment.MiddleRight:
							{
								Rectangle rcCentered = src.e_CenterTo(trg.e_GetCenter().e_ToPoint());
								ptLocation.Y = rcCentered.Y;
								break;
							}
					}

					switch (alignment)
					{
						case ContentAlignment.BottomLeft:
						case ContentAlignment.BottomCenter:
						case ContentAlignment.BottomRight:
							{
								ptLocation.Y = trg.Bottom - src.Height;
								if (offset.HasValue) ptLocation.Y -= offset.Value.Y;
								break;
							}
					}
				}

				{
					//Calculate X

					switch (alignment)
					{
						case ContentAlignment.TopLeft:
						case ContentAlignment.MiddleLeft:
						case ContentAlignment.BottomLeft:
							{
								ptLocation.X = trg.X;
								if (offset.HasValue) ptLocation.X += offset.Value.X;
								break;
							}
					}

					switch (alignment)
					{
						case ContentAlignment.TopCenter:
						case ContentAlignment.MiddleCenter:
						case ContentAlignment.BottomCenter:
							{
								Rectangle rcCentered = src.e_CenterTo(trg.e_GetCenter().e_ToPoint());
								ptLocation.X = rcCentered.X;
								break;
							}
					}

					switch (alignment)
					{
						case ContentAlignment.TopRight:
						case ContentAlignment.MiddleRight:
						case ContentAlignment.BottomRight:
							{
								ptLocation.X = trg.Right - src.Width;
								if (offset.HasValue) ptLocation.X -= offset.Value.X;
								break;
							}
					}
				}
				return new(ptLocation, src.Size);
			}

