							Result += STREAM_SEPARATOR + streamName + FULL_STREAM_PATH_SUFFIX;
							if (Result.Length >= PATH_LIMIT) Result = uom.WinAPI.IO.UNC_PATH_PREFIX + Result;
						}

						return Result;
